class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0;
        int n = s.size();
        int ans = 0;

        bool check = false;

        for(int i = n-1; i >= 0; i --){
            if(check == true and s[i] == '1'){
                cnt ++;
                check = false;
            }

            if(s[i] == '1') ans += cnt;
            if(s[i] == '0') check = true;
        }

        return ans;
    }
};
