class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        ans = 0
        if "1" not in s:
            return 0
        start = s.index("1")
        cur = 0
        while start < n:
            end = start
            while end + 1 < n and s[end + 1] == "1":
                end += 1
            if end == n - 1:
                break
            nxt_start = end + 1
            while nxt_start < n and s[nxt_start] == "0":
                nxt_start += 1
            cur += end - start + 1
            ans += cur
            start = nxt_start
        return ans
