class Solution {
public:
    int maxOperations(string s) {

        int n = s.size();

        int r = n - 1, ans = 0;

        // while(r >= 0 && s[r] == '1')r--;

        int cur = 0, last = -1;

        for(int i = n - 1; i >= 0; i--){

            if(s[i] == '1'){
                ans += cur;
            }
            else{

                if(i + 1 != last){
                    cur++;
                }
                last = i;
            }
        }

        return ans;
    }
};
