class Solution {
    public int maxOperations(String s) {
        int n = s.length();
        int idx = n;
        for (int i = n - 1; i >= 0; i--) {
            if (s.charAt(i) == '0') {
                break;
            } else {
                idx = i;
            }
        }
        int res = 0, zero = 0;
        for (int i = idx - 1; i >= 0; i--) {
            if (s.charAt(i) == '1') {
                res += zero;
            } else if (i == n - 1 || s.charAt(i + 1) == '1') {
                zero++;
            }
        }
        return res;
    }
}
