class Solution {
public:
    int maxOperations(string s) {

        int ans = 0;
        int cnt = 0;
        int idx = 0;
        bool z = false;
        for(auto x:s){

            if(x == '1'){
                if(z){
                    ans += cnt;
                }
                cnt++;
                z = false;
            }
            else
                z = true;
        }
        if(z){
            ans += cnt;
        }
        return ans;
    }
};
