class Solution {
public:
    int maxOperations(string s) {
        int n=s.length();
        int ops = 0;
        int ans = 0;
        bool flag = false;
        for(int i=n-1;i>=0;i--){
            // cout<<ans<<endl;
            if(i+1<n && s[i] == '1'){
                // cout<<ans<<endl;
                ops += ans;
            }
            if(s[i] == '0'){
                if(!flag)
                    ans++;
                flag = true;
            }
            else{
                flag = false;
            }
        }
        return ops;
    }
};
