class Solution {
public:
    int maxOperations(string s) {
        int flag=0,count=0,ans=0;
        int n=s.length();
        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                flag=1;
                count++;
            }
            else{
                if(flag==1){
                    flag=0;
                    ans+=count;
                }
            }
        }
        return ans;
    }
};
