class Solution {
public:
  int maxOperations(string s) {
    int ans = 0, cnt = 0;
    vector<int> ones;
    for (int i = 0; i < s.length(); i++) {
      if (s[i] == '1') {
        cnt++;
      } else {
        if (cnt) {
          ones.push_back(cnt);
        }
        cnt = 0;
      }
    }
    int active = 0;
    for (int i : ones) {
      active += i;
      ans += active;
    }
    return ans;
  }
};
