class Solution {
    public int maxOperations(String s) {

        int ones = 0;
        int ans = 0;
        for (int i = 0; i < s.length(); i++) {
            boolean one = s.charAt(i) == '1';
            if (one) {
                ones++;
            } else {
                continue;
            }
            if (i + 1 < s.length() && s.charAt(i + 1) == '0') {
                ans += ones;
                i++;
            }
        }
        return ans;

    }
}
