class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, ct = (s[0] == '1');
        for (int i = 1; i < s.size(); i++) {
            if (s[i] == '1')
                ct++;
            if (s[i] == '0' && s[i - 1] == '1')
                ans += ct;
        }
        return ans;
    }
};
