class Solution:
    def maxOperations(self, s: str) -> int:
        ones_pile = 0
        i = 0
        ans = 0
        while i < len(s):
            if s[i] == '1':
                ones_pile += 1
                i += 1
            else:
                next_i = i + 1
                while next_i < len(s) and s[next_i] == '0':
                    next_i += 1
                i = next_i
                ans += ones_pile
        return ans
