class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int cur = 0;
        char last = '0';
        for (auto x : s) {
            if (x == '1') ++cur;
            else if (last != '0') ans += cur;
            last = x;
        }
        return ans;
    }
};
