class Solution {
public:
  int maxOperations(string s) {
    int n = (int)s.size();
    string t = "";
    for(int i = 0; i < n; i++) {
      if(s[i] == '1') {
        t += '1';
      } else if(i == 0 || s[i - 1] == '1') {
        t += '0';
      }
    }
    int m = (int)t.size();
    int ans = 0;
    int cntz = 0;
    for(int i = m - 1; i >= 0; i--) {
      if(t[i] == '0') {
        cntz++;
      } else {
        ans += cntz;
      }
    }
    return ans;
  }
};
