class Solution:
    def maxOperations(self, s: str) -> int:
        cnt = 0
        ans = 0
        n = len(s)
        for i, c in enumerate(s):
            if c == '0':
                continue
            else:
                if i > 0 and s[i - 1] == '0':
                    ans += cnt
                cnt += 1
        if s[n - 1] == '0':
            ans += cnt
        return ans
