public class Solution {
    public int MaxOperations(string s) {
        int n = s.Length;
        bool f = false;
        int ones = 0, sum = 0;
        for (int i = 0; i < n; ++i) {
            if (s[i] == '1') { f = true; ++ones; }
            else if (f) { sum += ones; f = false; }
        }
        // if (s[^1] == '0') sum += ones;
        return sum;
    }
}
