class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(), ans = 0, cnt = 0;
        for (int i = n; i --> 0;) {
            if (s[i] == '0' && (i == n - 1 || s[i + 1] == '1')) cnt++;
            if (s[i] == '1') ans += cnt;
        }
        return ans;
    }
};
