class Solution {

    public int maxOperations(String s) {
        int count = 0;
        for (int i = 1, j = 0; i < s.length(); i++) {
            j += s.charAt(i - 1) == '1' ? 1 : 0;
            count += s.charAt(i - 1) == '1' && s.charAt(i) == '0' ? j : 0;
        }
        return count;
    }
}
