class Solution {
public:
    int maxOperations(string s) {
        int ones = 0;
        int res = 0;
        for(int i  = 0 ; i < s.size();i++){
            if(s[i]=='0'){
                while(i<s.size() && s[i]=='0')i++;
                if(s[i]=='1')i--;
                res+=ones;
            }
            else ones++;
        }
        return res;
    }
};
