class Solution {
public:
    int maxOperations(string s) {
        bool flag = false;
        int n = s.size(), k = 0, ans = 0;
        for (int i = n - 1; i >= 0; i--)
        {
            if (s[i] == '0')
            {
                if (!flag)
                {
                    k++;
                    flag = true;
                }
            }
            else
            {
                ans += k;
                flag = false;
            }
        }
        return ans;
    }
};
