class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0;
        int one = 0;

        int i=0;
        while(i<s.size()){
            if(s[i] == '1'){
                one++;
                i++;
            }
            else{
                cnt+=one;
                while(i<s.size() && s[i] == '0'){
                    i++;
                }
            }
        }

        return cnt;
    }
};
