class Solution {
    public int maxOperations(String s) {
        int c = 0, ans = 0;
        for (int i = 0; i < s.length(); ) {
            if (s.charAt(i) == '1') {
                c++;
                i++;
            } else {
                int c0 = 0;
                while (i < s.length() && s.charAt(i) == '0') {
                    i++;
                    c0++;
                }
                if (c0 > 0) {
                    ans += c;
                }
            }
        }
        return ans;
    }
}
