class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int cnt = 0;
        int ans = 0;
        for (int i = 0; i + 1 < s.size(); i++) {
            if (s[i] == '1') {
                cnt++;
                if (s[i + 1] == '0') {
                    ans += cnt;
                }
            }
        }
        return ans;
    }
};
