class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, cnt = 0, pv = 1;
        for(char c : s) {
            if(c == '1') {
                ++cnt; pv = 1;
            }
            else if(pv){
                ans += cnt; pv = 0;
            }
        }

        return ans;
    }
};
