func maxOperations(s string) int {
    cnt := 0
    ans := 0
    i := 0
    for len(s) > 0 && s[len(s) - 1] == '1' {
        s = s[:len(s)-1]
    }
    for i < len(s) {
        if s[i] == '1' {
            cnt++
            i++
            continue
        } 
        for i + 1 < len(s) && s[i+1] == '0' {
            i++
        }
        ans += cnt
        i++
    }
    return ans
}
