class Solution {
public:
    int maxOperations(string s) {
        int a=0,n=s.size(),ans=0;
        for(int i=0;i<n-1;i++){
            if(s[i]=='1'){
                a++;
                if(s[i+1]=='0'){
                    ans+=a;
                }
            }
        }
        return ans;
    }
};
