class Solution {
public:
    int maxOperations(string s) {
        using ll = long long;
        ll ans = 0;
        ll c = 0;
        string x;
        for(auto it:s){
            if(x.size() == 0)x += it;
            else{
                if(it == '1')x += it;
                else{
                    if(x.back() == '1')x += it;
                }
            }
        }
        s = x;
        reverse(s.begin() , s.end());
                for(auto it:s){
                    if(it == '0'){
                        c++;
                    }else{
                        ans += c;
                    }
                }
                return ans;

    }
};
