class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int cnt = 0;
        bool lft = false;
        for(char x : s){
            if(x=='1') cnt++;
        }
        for(int i = s.size()-1;i >= 0;i--) {
            if(s[i]=='0' && !lft) ans += cnt, lft = true;
            if(s[i]=='1') cnt--, lft = false;
        }
        return ans;
    }
};
