ll gcd(ll a, ll b)
{
    return b == 0 ? a : gcd(b, a % b);
}

ll lcm(ll a, ll b)
{
    return (a * b) / gcd(a, b);
}

class Solution
{
public:
    int maxOperations(string s)
    {
        ll ones = 0, cnt = 0, ans = 0;
        s += '*';
        char temp = s[0];
        for (auto it : s)
        {
            if (temp != it)
            {
                if (temp == '1' && it != '*')
                {
                    ones += cnt;
                    ans += ones;
                }
                temp = it;
                cnt = 1;
            }
            else
            {
                cnt++;
            }
        }
        return ans;
    }
};
