class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int cnt = 0;
        int ops = 0;
        for(int i = 0; i + 1 < n; i++) {
            if(s[i] == '1' and s[i + 1] == '0') {
                cnt++;
                ops += cnt;
            } else if(s[i] == '1') {
                cnt++;
            }
        }

        return ops;
    }
};
