class Solution {
public:
    int maxOperations(string s) {
        int res=0,cnt=0,vis=0;
        s+="1";
        for(auto c:s){
            if(c=='0') vis=1;
            else{
                if(vis) res+=cnt;
                cnt++;
                vis=0;
            }
        }
        return res;
    }
};
