class Solution:
    def maxOperations(self, s: str) -> int:
        max_ops = 0
        i = 0
        n = len(s)
        num_one = 0

        while i < n - 1:
            if s[i] == '1':
                num_one += 1
            if s[i] == '1' and s[i + 1] == '0':
                max_ops += num_one
                # 将1移动到右边
                i += 2  # 跳过下一个已经被1覆盖的0
            else:
                i += 1

        return max_ops
