class Solution {
public:
    int maxOperations(string s) {
        s += "1";
        int ans = 0;
        int ones = 0;
        for(int i = 0;i < s.size();i++){
            if(i > 0 and s[i] == '1' and s[i - 1] == '0'){
                ans += ones;
            }

            if(s[i] == '1')ones++;
        }
        return ans;
    }
};
