class Solution {
public:
    int maxOperations(string s) {
        bool one = true;
        int zeros = 0;
        int result = 0;
        for (int i = s.size() - 1; i >= 0; --i) {
            if (one) {
                if (s[i] == '0') {
                    one = false;
                    ++zeros;
                } else {
                    result += zeros;
                }
            } else {
                if (s[i] == '1') {
                    one = true;
                    result += zeros;
                }
            }
        }

        return result;
    }
};
