class Solution {
public:
    int maxOperations(string s) {
        const int n = (int)s.size();\
        int z = 0;
        int ans = 0;
        for (int i = n - 1; i >= 0; --i) {
            if (s[i] == '0' && (i == n - 1 || s[i+1] == '1')) {
                z++;
            }
            if (s[i] == '1') {
                ans += z;
            }
        }
        return ans;
    }
};
