class Solution {
    public int maxOperations(String s) {
        char[] chars = s.toCharArray();
        int ans = 0;
        int count0 = 0;
        for (int i = chars.length-1; i >= 0; --i) {
            char c = chars[i];
            char d = i == chars.length-1 ? '1' : chars[i+1];
            if (c == '0') {
                if (d == '1') ++count0;
            }
            else {
                ans += count0;
            }
        }
        return ans;
    }
}
