class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        int cnt = 0;
        int ans = 0;
        for(int i = 0; i < n;) {
            if(s[i] == '1') cnt++, i++;
            else {
                int j = i;
                while(j < n && s[j] == '0') j++;
                ans += cnt;
                i = j;
            }
        }
        return ans;
    }
};
