class Solution {
public:
    int maxOperations(string s) {
        int n = (int)s.size(), ans = 0, c = 0, S = 0;
    for(int i = 0; i < n; i++)
    {
        if(s[i] == '0' && c)
        {
            S += c;
            ans += S;
            c = 0;
        }
        else if(s[i] == '1') c++;
    }
    return ans;
    }
};
