class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int cnt = 0;
        int count = 0;
        bool flag = true;
        for(int i = 0 ; i < n ; i++)
        {
            if(s[i] == '1')count++ , flag = true;
            else
            {
                if(flag)cnt += (count);
                flag = false;
            }
        }
        return cnt;
    }
};
