class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        s = " " + s;
        s += '1';
        int cnt = 0, lst = 0;
        int ans = 0;
        for(int i = 1;i <= n + 1;i++) {
            if(s[i] == '1') {
                int dist = i - lst - 1;
                if(dist) ans += cnt;
                lst = i;
                ++cnt;
            }
        }
        return ans;
    }
};
