class Solution {
    public int maxOperations(String s) {
        int res = 0;
        int oneCount = 0;
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '1') {
                oneCount++;
            }
            if (i + 1 < s.length() && s.charAt(i) == '1' && s.charAt(i+1) == '0') {
                res += oneCount;
            }
        }
        return res;
    }
}
