class Solution {
public:
    int maxOperations(string s) {
        int n=s.length();
        s+='1';
        n=s.length();
        vector<int>cnt(n+1);
        for(int i=n-1;i>=0;i--)
        {
            cnt[i]=cnt[i+1];
            if(s[i]=='0'&&s[i+1]=='1')
            {
                cnt[i]++;
            }
        }
        int ans=0;
        for(int i=0;i<n;i++)
        {
            if(s[i]=='1')
            {
                ans+=cnt[i];
            }
        }
        return ans;
    }
};
