class Solution {
public:
    int maxOperations(string s) {
        int ones = 0;
        int maxOp = 0;
        bool flag = false;
        for(auto ch:s){
            if(ch == '1'){
                ones++;
                flag = true;
            }
            else if(flag){
                maxOp+=ones;
                flag = false;
            }
        }
        return maxOp;
    }
};
