class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int c = 0, one = 0;
        int64_t ans = 0;
        for (int l = 0, r; l < n; l = r) {
            for (r = l + 1; s[r] == s[l]; r++) {}
            one += s[l] == '1'? r-l:0;
            if(s[l]=='0') ans+=one;
        }
        return ans;
    }
};
