class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int ans = 0;
        int cnt = 0;
        if (n == 0)
            return 0;

        if (s[0] == '1')
            cnt++;
        for (int i = 1; i < n; i++)
        {
            if ((s[i] == '1' && s[i - 1] == '0') || 
            (i == n - 1 && s[i] == '0'))
            {
                ans += cnt;
            }
            if (s[i] == '1')
                cnt++;
        }
        return ans;
    }
};
