class Solution {
public:
    int maxOperations(string s) {
        int temp=0, ans=0;
        bool n=1;
        for (auto x: s){
            if (x=='0'){
                n=0; 
            }
            else{
                if(!n){
                    ans+= temp;
                    n = 1;
                }
                temp++;
            }
        }
        if(!n){
            ans+= temp;
        }
        return ans;
    }
};
