class Solution {
public:
    int maxOperations(string s) {
        int onec = 0;
        int i=0;
        int ans = 0;
        while(i<s.length()){
            if(s[i]=='1'){
                onec++;
                i++;
            }
            else{
                while(i<s.length() && s[i]=='0'){
                    i++;
                }
                ans += onec; 
            }
        }
        return ans;
    }
};
