import java.util.*;

class Solution {
    public int maxOperations(String s) {
        List<Integer> l = new ArrayList<>();
        for(int i = 0; i < s.length(); i++) {
            if(s.charAt(i) == '1') {
                l.add(1);
            }
            else if(l.size() == 0 || l.get(l.size() - 1) == 1){
                l.add(0);
            }
        }
        int ans = 0, ones = 0;
        for(int x : l) {
            if(x == 0) {
                ans += ones;
            }
            else {
                ones++;
            }
        }
        return ans;
    }
}
