class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int one=0;
        int flag=0;
        for(int i=0;i<s.length();i++){
            if(s[i]=='1'){
                one++;
                flag=1;
                continue;
            }
            if(flag==1 && s[i]=='0'){
                ans+=one;
                flag=0;
            }


        }
        return ans;
    }
};
