class Solution {
public:
    int maxOperations(string s) {

       string ans = "";
       bool f = 0;
       for(int i=0;i<s.size();i++)
       {
        if(s[i]=='1')
        {
        ans+=s[i];
        f = 0;
        }
        else
        {
          if(f==0)
          {
            ans+=s[i];
            f = 1;
          }
        }
       }
       int cnt = 0;
       for(int i=0;i<ans.size();i++)
       {
        if(ans[i]=='1')
        cnt++;
       }

        int res = 0;
       for(int i=ans.size()-1;i>=0;i--)
       {
          if(ans[i]=='1')
          cnt--;
          else
          {
            res+=cnt;
          }
       }

       return res;
    }
};
