class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        vector<int> pre(n,0);
        if(s[0]=='1') pre[0]++;
        for(int i=1;i<n;i++)
        {
            pre[i] = pre[i-1] + (s[i]=='1' ? 1 : 0);
        }
        int c = 0;
        for(int i=1;i<n;i++)
        {
            if(s[i]=='1' && s[i-1]=='0') c+=pre[i-1];
        }
        if(s[n-1]=='0' && n>=2) c+=(pre[n-2]);
        return c;
    }
};
